        border-radius: 5px;
        text-decoration: none;
    }

    .triangledash {
        position: absolute;
        bottom: -7px;
        right: 1px;
        width: 0;
        height: 0;
        border-left: 30px solid transparent;
        /* Adjust the size of the triangle */
        border-top: 30px solid #639;
        /* Adjust the size of the triangle and its color */
        transform: translate(100%, 100%);
        opacity: 0;
        /* Initially hide the triangle */
        transition: opacity 0.3s;
        /* Add a transition for smooth hover effect */
        transform: rotate(90deg);
    }

    .triangle {
        position: absolute;
        bottom: -7px;
        right: 2px;
        width: 0;
        height: 0;
        border-left: 30px solid transparent;
        /* Adjust the size of the triangle */
        border-top: 30px solid #639;
        /* Adjust the size of the triangle and its color */
        transform: translate(100%, 100%);
        opacity: 0;
        /* Initially hide the triangle */
        transition: opacity 0.3s;
        /* Add a transition for smooth hover effect */
        transform: rotate(90deg);
    }

    .triangle-container:hover .triangle {
        opacity: 1;
        /* Display the triangle on hover */
    }

    .triangle-container:hover .triangledash {
        opacity: 1;
        /* Display the triangle on hover */
    }

    .triangle-container:hover .hovercolor {
        color: #639;
        /* Set the color to #639 */
    }

    .custom-image {
        max-width: 100%; /* Ensure image doesn't exceed its container's width */
        height: 30px; /* Maintain aspect ratio */
        width: 30px; /* Set the width to 50% of its container */
    }
    .text{
        font-size: 13px;
    }
